mapper.insert:
INSERT INTO product
    ( prodcode
    , prodname
    , price
    , quantity)
VALUES 
    ( #{prodCode}
     ,#{prodName}
     ,#{price}
     ,#{quantity} )

mapper.update:
UPDATE product p     
  
      p.MODDATE  = sysdate
      
     , p.PRODNAME = #{prodName}
      
      
     , p.PRICE    = #{price}
      
      
     , p.QUANTITY = #{quantity}
          
  
     
 WHERE p.PRODCODE = #{prodCode}

mapper.select:
SELECT p.prodcode   
  FROM product p    
 WHERE p.prodcode = #{prodCode}
